async fn scan_service(central: &Adapter) -> Option<Peripheral> {

    let mut timeout = 30;

    while timeout > 0 {
        time::sleep(Duration::from_secs(1)).await;

        println!("⏳ Trying to auto-discover a printer...");

        for p in central.peripherals().await.unwrap() {
            let properties = p.properties().await.unwrap().unwrap();
            let service = properties.services.iter().any(|s| POSSIBLE_SERVICE_UUIDS.contains(&&s.to_string()[..]));

            if service {
                return Some(p)
            }
        }

        timeout -= 1;
    }

    None

}
